  // prevUser,
  // setPrevUser,
  previous,
  setPrevious,
}) {
  const [showUser, setShowUser] = useState(true);
  useEffect(() => {
    if (previous == user) {
      setShowUser(false);
    } else {
      setShowUser(true);
      setPrevious((previouse) => user);
    }
    setPrevious((previouse) => user);
  }, []);
  console.log(showUser);
  console.log(user);
  console.log(previous);
  return (
    <>
      <p>{showUser ? "yes" : "no"}</p>
      <div
        className={
          deviceUser == user
            ? "message-container user-message-container"
            : "message-container"
        }
      >
        <p className="message">{message}</p>
      </div>
    </>
  );
}

export default Message;
