import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/message.css";

function Message({
  message,
  user,
  _id,
  deviceUser,
  userCounter,
  setUserCounter,
  prevuser,
  setPrevUser,
}) {
  const userDeprication = () => {
    if (prevuser == user) {
      setUserCounter(userCounter + 1);
    } else {
      setUserCounter(1);
      setPrevUser(user);
    }
  };
  userDeprication();
  return (
    <div
      className={
        deviceUser == user
          ? "message-container user-message-container"
          : "message-container"
      }
    >
      <p className="message">{message}</p>
    </div>
  );
}

export default Message;
